import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Components
import SignLogin from './components/signLogin';
import Dashboard from './components/dashboard';
import CustomerProfile from './components/CustomerProfile';
import { userService } from './services/apiService';

// Protected Route Component
const ProtectedRoute = ({ element, allowedRoles }) => {
    const isLoggedIn = userService.isLoggedIn();
    const currentUser = userService.getCurrentUser();
    
    if (!isLoggedIn) {
        return <Navigate to="/login" />;
    }
    
    if (allowedRoles && !allowedRoles.includes(currentUser.role)) {
        return <Navigate to="/dashboard" />;
    }
    
    return element;
};

// Customer Route Component
const CustomerRoute = ({ children }) => {
    const isLoggedIn = userService.isLoggedIn();
    const isCustomer = userService.isCustomer();
    
    if (!isLoggedIn) {
        return <Navigate to="/login" />;
    }
    
    if (!isCustomer) {
        return <Navigate to="/dashboard" />;
    }
    
    return children;
};

// Auth Route Component
const AuthRoute = ({ children }) => {
    const location = useLocation();
    const isLogin = location.pathname === '/login';
    
    if (userService.isLoggedIn()) {
        return <Navigate to="/dashboard" />;
    }
    
    return React.cloneElement(children, { isLogin });
};

function App() {
    const [loading, setLoading] = useState(true);
    
    useEffect(() => {
        const checkAuthStatus = async () => {
            try {
                if (userService.isLoggedIn()) {
                    await userService.getProfile();
                }
            } catch (error) {
                userService.logout();
            } finally {
                setLoading(false);
            }
        };
        
        checkAuthStatus();
    }, []);
    
    if (loading) {
        return <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</div>;
    }
    
    return (
        <Router>
            <ToastContainer position="top-right" autoClose={3000} />
            <Routes>
                {/* Dashboard as default route */}
                <Route path="/" element={<Navigate to="/dashboard" />} />
                
                {/* Auth routes */}
                <Route path="/login" element={<AuthRoute><SignLogin /></AuthRoute>} />
                <Route path="/register" element={<AuthRoute><SignLogin /></AuthRoute>} />
                
                {/* Protected routes */}
                <Route path="/dashboard" element={<ProtectedRoute element={<Dashboard />} />} />
                <Route path="/profile" element={<CustomerRoute><CustomerProfile /></CustomerRoute>} />
                
                {/* Catch all route */}
                <Route path="*" element={<Navigate to="/dashboard" />} />
            </Routes>
        </Router>
    );
}

export default App;
