import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Komponen
import SignLogin from './components/signLogin';
import Dashboard from './components/dashboard';
import {userService} from './services/apiService';

// Protected Route Component
const ProtectedRoute = ({ element, allowedRoles }) => {
    const isLoggedIn = userService.isLoggedIn();
    const currentUser = userService.getCurrentUser();

    // Cek apakah user sudah login
    if (!isLoggedIn) {
        return <Navigate to="/" />;
    }

    // Jika ada pembatasan role, cek role user
    if (allowedRoles && !allowedRoles.includes(currentUser.role)) {
        return <Navigate to="/dashboard" />;
    }

    return element;
};

function App() {
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Cek status login saat komponen dimuat
        const checkAuthStatus = async () => {
            try {
                if (userService.isLoggedIn()) {
                    // Validasi token dengan mengambil profil
                    await userService.getProfile();
                }
            } catch (error) {
                // Jika token tidak valid, logout
                userService.logout();
            } finally {
                setLoading(false);
            }
        };

        checkAuthStatus();
    }, []);

    if (loading) {
        return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
    }

    return (
        <Router>
            <ToastContainer position="top-right" autoClose={3000} />
            <Routes>
                <Route 
                    path="/" 
                    element={userService.isLoggedIn() ? <Navigate to="/dashboard" /> : <SignLogin />} 
                />
                <Route 
                    path="/dashboard" 
                    element={<ProtectedRoute element={<Dashboard />} />} 
                />
                <Route path="" element={<Navigate to="/" />} />
            </Routes>
        </Router>
    );
}

export default App;
