import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000/api';

// Token Management
const getToken = () => localStorage.getItem('token');
const setToken = (token) => localStorage.setItem('token', token);
const removeToken = () => localStorage.removeItem('token');

//User Management
const setUser = (user) => localStorage.setItem('user', JSON.stringify(user));
const getUser = () => {
    const userStr = localStorage.getItem('user');
    return userStr ? JSON.parse(userStr) : null;
};
const removeUser = () => localStorage.removeItem('user');

// Create Axios Instance
const api = axios.create({
    baseURL: API_BASE_URL,
    headers: {
        'Content-Type': 'application/json',
    },
});

// Add auth token interceptor
api.interceptors.request.use((config) => {
    const token = getToken();
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
});

// Product Service
export const productService = {
    getAllProducts: async () => {
        try {
            const res = api.get('/products/allproduct');
            return (await res).data;
        } catch (err) {
            throw err.response?.data || {message: 'Terjadi kesalahan saat memunculkan products'}
        }
    },

    getProductById: async (productId) => {
        try {
            const res = api.get(`/products/${productId}`);
            return (await res).data;
        } catch (err) {
            throw err.res?.data || {message: 'Terjadi kesalahan saat memunculkan products'}
        }
    },
}

// User Service
export const userService = {
    register: async (email, password, role = 'customer') => {
        try {
            const response = await api.post('/auth/register', {
                email,
                password,
                role
            });
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat register' };
        }
    },

    login: async (email, password) => {
        try {
            const response = await api.post('/auth/login', {
                email,
                password
            });
            const { token, user } = response.data;
            setToken(token);
            setUser(user);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat login' };
        }
    },

    logout: () => {
        removeToken();
        removeUser();
    },

    getProfile: async () => {
        try {
            const response = await api.get('/auth/profile');
            return response.data;
        } catch (error) {
            if (error.response?.status === 401) {
                userService.logout();
            }
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil profil' };
        }
    },

    getAllUsers: async () => {
        try {
            const response = await api.get('/auth/users');
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil data users' };
        }
    },

    isLoggedIn: () => !!getToken(),
    getCurrentUser: () => getUser(),
    isAdmin: () => {
        const user = getUser();
        return user && user.role === 'admin';
    },
    isOrganization: () => {
        const user = getUser();
        return user && user.role === 'organization';
    },
    isCustomer: () => {
        const user = getUser();
        return user && user.role === 'customer';
    }
};

export const customerProfileService = {

    
    getCustomerProfiles: async () => {
        try {
            const response = await api.get('/customers/allcustomer');
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil customer profiles' };
        }
    },

    getCustomerProfileById: async (user_id) => {
        try {
            const response = await api.get(`/customers/${user_id}`);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil customer profile' };
        }
    },

    createCustomerProfile: async (user_id, full_name, phone, address, delivery_instructions) => {
        try {
            const response = await api.post('/customers/create', {
                user_id,
                full_name,
                phone,
                address,
                delivery_instructions
            });
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat membuat customer profile' };
        }
    },
};

export default api;
