import React, { useState, useEffect } from 'react';
import { organizationProfileService, userService } from '../services/apiService';
import { useNavigate } from 'react-router-dom';
import '../style/orgPersonalisasiProfile.css';

const OrgPersonalisasiProfile = () => {
    const navigate = useNavigate();
    const [profile, setProfile] = useState({
        org_name: '',
        contact_person: '',
        contact_phone_org: '',
        org_type: 'retail' // default value
    });
    
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');

    useEffect(() => {
        const checkAuthAndLoadProfile = async () => {
            try {
                // Get current user info from JWT token
                const currentUser = userService.getCurrentUser();
                if (!currentUser || !userService.isOrganization()) {
                    navigate('/login');
                    return;
                }
                
                await loadProfile();
            } catch (error) {
                if (error.response?.status === 401) {
                    navigate('/login');
                } else {
                    setError(error.message || 'Error loading profile');
                }
            } finally {
                setLoading(false);
            }
        };
        
        checkAuthAndLoadProfile();
    }, [navigate]);

    const loadProfile = async () => {
        try {
            const res = await organizationProfileService.getOrgProfileById();
            if (res.data) {
                const setProfile = res.data;
                try {
                    setProfile.org_name = setProfile.org_name || ''; // Pastikan org_name tidak undefined
                    setProfile.contact_person = setProfile.contact_person || ''; // Pastikan contact_person tidak undefined
                    setProfile.contact_phone_org = setProfile.contact_phone_org.replace(/(\d{3})(\d{3})(\d+)/, '$1-$2-$3'); // Format nomor telepon
                    setProfile.org_type = setProfile.org_type || 'retail'; // Pastikan org_type tidak undefined 
                } catch (e) {
                    setProfile({
                        org_name: '',
                        contact_person: '',
                        contact_phone_org: '',
                        org_type: 'retail'
                    })
                }
            }
        } catch (error) {
            console.error("Error loading profile:", error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfile(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage('');
        
        try {
            const isLoggedIn = await userService.isLoggedIn();
            if (!isLoggedIn) {
                throw new Error('Silakan login terlebih dahulu');
            }

            // Langsung menggunakan createOrUpdateProfile
            const response = await organizationProfileService.createOrUpdateProfile(profile);
            
            if (response.status === 'success') {
                setSuccessMessage('Profile berhasil diperbarui!');
                await loadProfile(); // Reload profile untuk mendapatkan data terbaru
            } else {
                setError(response.message || 'Terjadi kesalahan saat memperbarui profil');
            }
        } catch (error) {
            if (error.response?.status === 401) {
                navigate('/login');
            } else {
                setError(error.message || 'Error updating profile');
            }
        }
    };

    const validatePhoneNumber = (e) => {
        // Hanya izinkan input berupa angka dan beberapa karakter khusus untuk format
        const value = e.target.value;
        // Izinkan: angka, tanda plus di awal, tanda kurung, spasi, dan tanda hubung
        if (!/^[0-9+() -]*$/.test(value)) {
            e.preventDefault();
            return;
        }
        handleChange(e);
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loader"></div>
                <div>Memuat data profil...</div>
            </div>
        );
    }

    return (
        <div className="org-profile-container">
            <h1 className="profile-title">
                Profil Organisasi
                <span className="title-decoration"></span>
            </h1>

            {error && (
                <div className="error-message">
                    <span className="error-icon">⚠️</span> {error}
                </div>
            )}
            
            {successMessage && (
                <div className="success-message">
                    <span className="success-icon">✅</span> {successMessage}
                </div>
            )}

            <form onSubmit={handleSubmit} className="profile-form">
                <div className="form-group">
                    <label className="form-label" htmlFor="org_name">Nama Organisasi</label>
                    <input
                        id="org_name"
                        type="text"
                        name="org_name"
                        value={profile.org_name}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label" htmlFor="contact_person">Contact Person</label>
                    <input
                        id="contact_person"
                        type="text"
                        name="contact_person"
                        value={profile.contact_person}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label" htmlFor="contact_phone_org">Nomor Telepon Organisasi</label>
                    <input
                        id="contact_phone_org"
                        type="tel"
                        name="contact_phone_org"
                        value={profile.contact_phone_org}
                        onChange={validatePhoneNumber}
                        className="form-input"
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label" htmlFor="org_type">Tipe Organisasi</label>
                    <select
                        id="org_type"
                        name="org_type"
                        value={profile.org_type}
                        onChange={handleChange}
                        className="form-select"
                        required
                    >
                        <option value="retail">Retail</option>
                        <option value="corporate">Corporate</option>
                        <option value="education">Education</option>
                        <option value="government">Government</option>
                        <option value="non_profit">Non Profit</option>
                    </select>
                </div>

                <div className="button-container">
                    <button
                        type="button"
                        onClick={() => navigate('/dashboard')}
                        className="back-button"
                        disabled={loading}
                    >
                        Kembali ke Dashboard
                    </button>
                    <button 
                        type="submit" 
                        className="save-button"
                        disabled={loading}
                    >
                        {loading ? 'Menyimpan...' : 'Simpan Profil'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default OrgPersonalisasiProfile;
