import axios from 'axios';

const api = axios.create({
    baseURL: process.env.REACT_APP_API_URL || 'http://localhost:3001',
    withCredentials: true // Enable sending cookies with requests
});

const apiService = {
    register: async (email, password, role) => {
        try {
            const response = await api.post('/auth/register', {
                email,
                password,
                role
            });
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat register' };
        }
    },

    login: async (email, password) => {
        try {
            const response = await api.post('/auth/login', {
                email,
                password
            });
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat login' };
        }
    },

    logout: async () => {
        try {
            await api.post('/auth/logout');
        } catch (error) {
            console.error('Error during logout:', error);
        }
    },

    getProfile: async () => {
        try {
            const response = await api.get('/auth/profile');
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil profil' };
        }
    },

    // Additional API endpoints can be added here
};

export default apiService;
