import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:8000/api',
    headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
    }
});

// Add an interceptor to add the token to requests
api.interceptors.request.use((config) => {
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
});

const userService = {
    register: async (email, password, role) => {
        try {
            const response = await api.post('/auth/register', {
                email,
                password,
                role
            });
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
            }
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat register' };
        }
    },

    login: async (email, password) => {
        try {
            const response = await api.post('/auth/login', {
                email,
                password
            });
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
            }
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat login' };
        }
    },

    logout: async () => {
        try {
            localStorage.removeItem('token');
            window.location.href = '/login'; // Force reload to clear any client state
        } catch (error) {
            console.error('Error during logout:', error);
        }
    },

    getProfile: async () => {
        try {
            const response = await api.get('/auth/profile');
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil profil' };
        }
    },

    getCurrentUser: async () => {
        try {
            const response = await api.get('/auth/current-user');
            return response.data.data;
        } catch (error) {
            return null;
        }
    },

    isLoggedIn: async () => {
        try {
            await api.get('/auth/profile');
            return true;
        } catch (error) {
            return false;
        }
    },

    getAllUsers: async () => {
        try {
            const response = await api.get('/auth/users');
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil data users' };
        }
    },

    isAdmin: async () => {
        const user = await userService.getCurrentUser();
        return user?.role === 'admin';
    },

    isCustomer: async () => {
        const user = await userService.getCurrentUser();
        return user?.role === 'customer';
    },

    isOrganization: async () => {
        const user = await userService.getCurrentUser();
        return user?.role === 'organization';
    }
};

const customerProfileService = {
    getCustomerProfileById: async () => {
        try {
            const response = await api.get('/api/customers/profile');
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil profil' };
        }
    },

    createCustomerProfile: async (full_name, phone, address, delivery_instructions) => {
        try {
            const response = await api.post('/api/customers/profile', {
                full_name,
                phone,
                address,
                delivery_instructions
            });
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat membuat profil' };
        }
    },

    updateCustomerProfile: async (full_name, phone, address, delivery_instructions) => {
        try {
            const response = await api.post('/api/customers/profile', {
                full_name,
                phone,
                address,
                delivery_instructions
            });
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengupdate profil' };
        }
    }
};

const productService = {
    getAllProducts: async () => {
        try {
            const response = await api.get('/api/products/allproduct');
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil data produk' };
        }
    },

    getProductById: async (productId) => {
        try {
            const response = await api.get(`/api/products/${productId}`);
            return response.data;
        } catch (error) {
            throw error.response?.data || { message: 'Terjadi kesalahan saat mengambil detail produk' };
        }
    }
};

export { userService, customerProfileService, productService };
