import axios from 'axios';

// Base URL untuk API
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api';

// Fungsi untuk menangani token
const getToken = () => localStorage.getItem('token');
const setToken = (token) => localStorage.setItem('token', token);
const removeToken = () => localStorage.removeItem('token');

// Menyimpan informasi user
const setUser = (user) => localStorage.setItem('user', JSON.stringify(user));
const getUser = () => {
    const userStr = localStorage.getItem('user');
    return userStr ? JSON.parse(userStr) : null;
};
const removeUser = () => localStorage.removeItem('user');

// Konfigurasi axios dengan token
const authHeader = () => {
    const token = getToken();
    return token ? { Authorization: `Bearer ${token}` } : {};
};

const userService = {
    register: async (email, password, role) => {
        try {
            const res = await axios.post(`${API_URL}/auth/register`, {
                email,
                password,
                role
            });
            if (res.data.token) {
                localStorage.setItem('token', res.data.token);
            }
            return res.data;
        } catch (err) {
            throw err.res?.data || { message: 'Terjadi kesalahan saat register' };
        }
    },

    login: async (email, password) => {
        try {
            const res = await axios.post(`${API_URL}/auth/login`, {
                email,
                password
            });
            // Simpan token dan user data
            const { token, user } = res.data;
            setToken(token);
            setUser(user);
            
            return res.data;
        } catch (err) {
            throw err.res?.data || { message: 'Terjadi kesalahan saat login' };
        }
    },

    // Logout user
    logout: () => {
        try {
            removeToken();
            removeUser();
        } catch (err) {
            throw err.res?.data || { message: 'Terjadi kesalahan saat logout' };
        }
    },

     // Mendapatkan semua users (admin only)
    getAllUsers: async () => {
        try {
            const res = await axios.get(`${API_URL}/auth/users`, {
                headers: authHeader()
            });
            return res.data;
        } catch (err) {
            throw err.res?.data || { message: 'Terjadi kesalahan saat mengambil data users' };
        }
    },

    getProfile: async () => {
        try {
            const res = await axios.get(`${API_URL}/auth/profile`, {
                headers: authHeader()
            });
            return res.data;
        } catch (err) {
            throw err.res?.data || { message: 'Terjadi kesalahan saat mengambil profil' };
        }
    },
    
    // Cek apakah user sudah login
    isLoggedIn: () => {
        return !!getToken();
    },
    
    // Mendapatkan user saat ini
    getCurrentUser: () => {
        return getUser();
    },
    
    // Cek apakah user adalah admin
    isAdmin: () => {
        const user = getUser();
        return user && user.role === 'admin';
    },
    
    // Cek apakah user adalah organization
    isOrganization: () => {
        const user = getUser();
        return user && user.role === 'organization';
    },
    
    // Cek apakah user adalah customer
    isCustomer: () => {
        const user = getUser();
        return user && user.role === 'customer';
    },

    toggleUserStatus: async (userId) => {
        try {
            const response = await axios.put(`${API_URL}/users/${userId}/toggle-status`, {}, {
                headers: authHeader()
            });
            return response.data;
        } catch (err) {
            throw err.response?.data || { message: 'Failed to toggle user status' };
        }
    }
};

const customerProfileService = {
    getCustomerProfileById: async () => {
        try {
            const res = await axios.get(`${API_URL}/customers/profile`, {
                headers: authHeader()
            });
            return res.data;
        } catch (err) {
            throw err.response?.data || { message: 'Terjadi kesalahan saat mengambil profil' };
        }
    },

    createOrUpdateProfile: async (profileData) => {
        try {
            const res = await axios.post(`${API_URL}/customers/profile-user`, {
                full_name: profileData.full_name,
                phone: profileData.phone,
                address: profileData.address,
                delivery_instructions: profileData.delivery_instructions
            }, {
                headers: authHeader()
            });
            return res.data;
        } catch (err) {
            throw err.response?.data || { message: 'Terjadi kesalahan saat memperbarui profil' };
        }
    }
};

const productService = {
    getAllProducts: async () => {
        try {
            const res = await axios.get(`${API_URL}/products/allproduct`);
            return res.data;
        } catch (err) {
            throw err.res?.data || { message: 'Terjadi kesalahan saat mengambil data produk' };
        }
    },

    getProductById: async (productId) => {
        try {
            const res = await axios.get(`${API_URL}/products/${productId}`);
            return res.data;
        } catch (err) {
            throw err.res?.data || { message: 'Terjadi kesalahan saat mengambil detail produk' };
        }
    }
};

export { userService, customerProfileService, productService };
