import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { userService } from '../services/apiService.js';

const SignLogin = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [role, setRole] = useState('customer');
    const [loading, setLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const [message, setMessage] = useState({ text: '', type: '' });
    const navigate = useNavigate();
    
    // CSS Styles
    const styles = {
        container: {
            minHeight: '100vh',
            background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '20px'
        },
        card: {
            width: '100%',
            maxWidth: '450px',
            background: '#fff',
            borderRadius: '12px',
            boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
            overflow: 'hidden'
        },
        header: {
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            padding: '30px',
            color: '#fff',
            textAlign: 'center'
        },
        headerTitle: {
            fontSize: '24px',
            fontWeight: '600',
            marginBottom: '8px'
        },
        headerSubtitle: {
            fontSize: '14px',
            opacity: '0.9'
        },
        content: {
            padding: '30px'
        },
        formGroup: {
            marginBottom: '20px'
        },
        label: {
            display: 'block',
            fontSize: '14px',
            fontWeight: '500',
            color: '#4a5568',
            marginBottom: '8px'
        },
        inputContainer: {
            position: 'relative'
        },
        input: {
            width: '100%',
            padding: '12px 16px',
            paddingRight: '40px',
            border: '1px solid #e2e8f0',
            borderRadius: '8px',
            fontSize: '14px',
            transition: 'all 0.3s ease',
            boxSizing: 'border-box'
        },
        inputFocus: {
            outline: 'none',
            borderColor: '#667eea',
            boxShadow: '0 0 0 2px rgba(102, 126, 234, 0.2)'
        },
        inputIcon: {
            position: 'absolute',
            right: '12px',
            top: '50%',
            transform: 'translateY(-50%)',
            color: '#a0aec0'
        },
        togglePassword: {
            position: 'absolute',
            right: '12px',
            top: '50%',
            transform: 'translateY(-50%)',
            background: 'none',
            border: 'none',
            color: '#a0aec0',
            cursor: 'pointer',
            padding: '0'
        },
        roleContainer: {
            marginBottom: '25px'
        },
        roleOptions: {
            display: 'grid',
            gridTemplateColumns: 'repeat(3, 1fr)',
            gap: '12px',
            marginTop: '8px'
        },
        roleOption: {
            position: 'relative'
        },
        roleRadio: {
            position: 'absolute',
            opacity: '0',
            width: '0',
            height: '0'
        },
        roleLabel: {
            display: 'block',
            padding: '12px',
            border: '1px solid #e2e8f0',
            borderRadius: '8px',
            textAlign: 'center',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            fontSize: '14px'
        },
        roleLabelSelected: {
            borderColor: '#667eea',
            backgroundColor: '#ebf4ff',
            color: '#667eea',
            fontWeight: '500'
        },
        roleCheck: {
            position: 'absolute',
            top: '4px',
            right: '4px',
            width: '16px',
            height: '16px',
            borderRadius: '50%',
            background: '#667eea',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: '#fff',
            fontSize: '10px',
            transition: 'opacity 0.3s ease'
        },
        submitButton: {
            width: '100%',
            padding: '14px',
            background: '#667eea',
            color: '#fff',
            border: 'none',
            borderRadius: '8px',
            fontSize: '16px',
            fontWeight: '500',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
        },
        submitButtonHover: {
            background: '#5a67d8',
            boxShadow: '0 4px 12px rgba(102, 126, 234, 0.3)'
        },
        submitButtonDisabled: {
            background: '#a0aec0',
            cursor: 'not-allowed',
            boxShadow: 'none'
        },
        spinner: {
            animation: 'spin 1s linear infinite',
            marginRight: '8px'
        },
        toggleForm: {
            textAlign: 'center',
            marginTop: '20px',
            fontSize: '14px',
            color: '#718096'
        },
        toggleButton: {
            background: 'none',
            border: 'none',
            color: '#667eea',
            fontWeight: '500',
            cursor: 'pointer',
            padding: '0',
            marginLeft: '4px'
        },
        divider: {
            display: 'flex',
            alignItems: 'center',
            margin: '25px 0'
        },
        dividerLine: {
            flex: '1',
            height: '1px',
            background: '#e2e8f0'
        },
        dividerText: {
            padding: '0 12px',
            color: '#a0aec0',
            fontSize: '12px'
        },
        socialButtons: {
            display: 'grid',
            gridTemplateColumns: 'repeat(2, 1fr)',
            gap: '12px'
        },
        socialButton: {
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            padding: '10px',
            border: '1px solid #e2e8f0',
            borderRadius: '8px',
            background: '#fff',
            color: '#4a5568',
            fontSize: '14px',
            cursor: 'pointer',
            transition: 'all 0.3s ease'
        },
        socialButtonHover: {
            background: '#f8fafc'
        },
        socialIcon: {
            marginRight: '8px',
            width: '18px',
            height: '18px'
        },
        message: {
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            fontSize: '14px',
            textAlign: 'center'
        },
        successMessage: {
            background: '#f0fff4',
            color: '#2f855a',
            border: '1px solid #c6f6d5'
        },
        errorMessage: {
            background: '#fff5f5',
            color: '#c53030',
            border: '1px solid #fed7d7'
        },
        '@keyframes spin': {
            from: { transform: 'rotate(0deg)' },
            to: { transform: 'rotate(360deg)' }
        }
    };

    const toggleForm = () => {
        setIsLogin(!isLogin);
        setEmail('');
        setPassword('');
        setConfirmPassword('');
        setRole('customer');
        setMessage({ text: '', type: '' });
    };
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage({ text: '', type: '' });
        
        try {
            if (isLogin) {
                const result = await userService.login(email, password, role);
                setMessage({ text: result.message || 'Login successful', type: 'success' });
                
                // Redirect based on role
                if (role === 'admin') {
                    navigate('/admin/dashboard');
                } else if (role === 'organization') {
                    navigate('/org/dashboard');
                } else {
                    navigate('/dashboard');
                }
            } else {
                if (password !== confirmPassword) {
                    setMessage({ text: 'Password and confirmation do not match', type: 'error' });
                    setLoading(false);
                    return;
                }
                
                const result = await userService.register(email, password, role);
                setMessage({ text: result.message || 'Registration successful', type: 'success' });
                setIsLogin(true);
                setEmail('');
                setPassword('');
                setConfirmPassword('');
            }
        } catch (error) {
            setMessage({ text: error.message || 'An error occurred', type: 'error' });
            console.error('Auth error:', error);
        } finally {
            setLoading(false);
        }
    };
    
    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <div style={styles.header}>
                    <h2 style={styles.headerTitle}>
                        {isLogin ? 'Welcome Back!' : 'Join Us Today'}
                    </h2>
                    <p style={styles.headerSubtitle}>
                        {isLogin ? 'Login to access your account' : 'Create a new account'}
                    </p>
                </div>
                
                <div style={styles.content}>
                    {message.text && (
                        <div style={{
                            ...styles.message,
                            ...(message.type === 'success' ? styles.successMessage : styles.errorMessage)
                        }}>
                            {message.text}
                        </div>
                    )}
                    
                    <form onSubmit={handleSubmit}>
                        {/* Email Field */}
                        <div style={styles.formGroup}>
                            <label htmlFor="email" style={styles.label}>
                                Email Address
                            </label>
                            <div style={styles.inputContainer}>
                                <input
                                    type="email"
                                    id="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    style={styles.input}
                                    onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
                                    onBlur={(e) => e.target.style = styles.input}
                                    placeholder="your@email.com"
                                    required
                                />
                                <div style={styles.inputIcon}>
                                    <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
                                        <path d="M2.003 5.884L10 9.882l7.997-3.998A2 2 0 0016 4H4a2 2 0 00-1.997 1.884z" />
                                        <path d="M18 8.118l-8 4-8-4V14a2 2 0 002 2h12a2 2 0 002-2V8.118z" />
                                    </svg>
                                </div>
                            </div>
                        </div>
                        
                        {/* Password Field */}
                        <div style={styles.formGroup}>
                            <label htmlFor="password" style={styles.label}>
                                Password
                            </label>
                            <div style={styles.inputContainer}>
                                <input
                                    type={showPassword ? "text" : "password"}
                                    id="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    style={styles.input}
                                    onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
                                    onBlur={(e) => e.target.style = styles.input}
                                    placeholder="••••••••"
                                    required
                                />
                                <button
                                    type="button"
                                    style={styles.togglePassword}
                                    onClick={() => setShowPassword(!showPassword)}
                                >
                                    <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                                        {showPassword ? (
                                            <>
                                                <path strokeWidth="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                                <path strokeWidth="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                                            </>
                                        ) : (
                                            <>
                                                <path strokeWidth="2" d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" />
                                            </>
                                        )}
                                    </svg>
                                </button>
                            </div>
                        </div>
                        
                        {/* Confirm Password Field (only for register) */}
                        {!isLogin && (
                            <div style={styles.formGroup}>
                                <label htmlFor="confirmPassword" style={styles.label}>
                                    Confirm Password
                                </label>
                                <div style={styles.inputContainer}>
                                    <input
                                        type={showConfirmPassword ? "text" : "password"}
                                        id="confirmPassword"
                                        value={confirmPassword}
                                        onChange={(e) => setConfirmPassword(e.target.value)}
                                        style={styles.input}
                                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
                                        onBlur={(e) => e.target.style = styles.input}
                                        placeholder="••••••••"
                                        required
                                    />
                                    <button
                                        type="button"
                                        style={styles.togglePassword}
                                        onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                                    >
                                        <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                                            {showConfirmPassword ? (
                                                <>
                                                    <path strokeWidth="2" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                                                    <path strokeWidth="2" d="M2.458 12C3.732 7.943 7.523 5 12 5c4.478 0 8.268 2.943 9.542 7-1.274 4.057-5.064 7-9.542 7-4.477 0-8.268-2.943-9.542-7z" />
                                                </>
                                            ) : (
                                                <>
                                                    <path strokeWidth="2" d="M13.875 18.825A10.05 10.05 0 0112 19c-4.478 0-8.268-2.943-9.543-7a9.97 9.97 0 011.563-3.029m5.858.908a3 3 0 114.243 4.243M9.878 9.878l4.242 4.242M9.88 9.88l-3.29-3.29m7.532 7.532l3.29 3.29M3 3l3.59 3.59m0 0A9.953 9.953 0 0112 5c4.478 0 8.268 2.943 9.543 7a10.025 10.025 0 01-4.132 5.411m0 0L21 21" />
                                                </>
                                            )}
                                        </svg>
                                    </button>
                                </div>
                            </div>
                        )}
                        
                        {/* Role Selection (only for register) */}
                        {!isLogin && (
                            <div style={styles.roleContainer}>
                                <label style={styles.label}>
                                    Account Type
                                </label>
                                <div style={styles.roleOptions}>
                                    {['customer', 'organization', 'admin'].map((r) => (
                                        <div key={r} style={styles.roleOption}>
                                            <input
                                                type="radio"
                                                id={r}
                                                name="role"
                                                checked={role === r}
                                                onChange={() => setRole(r)}
                                                style={styles.roleRadio}
                                            />
                                            <label
                                                htmlFor={r}
                                                style={{
                                                    ...styles.roleLabel,
                                                    ...(role === r ? styles.roleLabelSelected : {})
                                                }}
                                            >
                                                {r.charAt(0).toUpperCase() + r.slice(1)}
                                            </label>
                                            <div style={{
                                                ...styles.roleCheck,
                                                opacity: role === r ? 1 : 0
                                            }}>
                                                <svg width="10" height="10" viewBox="0 0 20 20" fill="currentColor">
                                                    <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                                                </svg>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                        
                        {/* Submit Button */}
                        <button
                            type="submit"
                            disabled={loading}
                            style={{
                                ...styles.submitButton,
                                ...(!loading ? styles.submitButtonHover : {}),
                                ...(loading ? styles.submitButtonDisabled : {})
                            }}
                            onMouseEnter={(e) => !loading && (e.target.style.background = '#5a67d8')}
                            onMouseLeave={(e) => !loading && (e.target.style.background = '#667eea')}
                        >
                            {loading ? (
                                <span style={{ display: 'flex', alignItems: 'center' }}>
                                    <svg style={styles.spinner} width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                                        <circle cx="12" cy="12" r="10" strokeWidth="4" strokeOpacity="0.25"></circle>
                                        <path d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" strokeWidth="4"></path>
                                    </svg>
                                    {isLogin ? 'Logging in...' : 'Registering...'}
                                </span>
                            ) : isLogin ? (
                                'Login'
                            ) : (
                                'Register'
                            )}
                        </button>
                    </form>
                    
                    {/* Toggle between login/register */}
                    <div style={styles.toggleForm}>
                        <span>
                            {isLogin ? "Don't have an account? " : 'Already have an account? '}
                        </span>
                        <button
                            onClick={toggleForm}
                            style={styles.toggleButton}
                        >
                            {isLogin ? 'Register here' : 'Login here'}
                        </button>
                    </div>
                    
                    {/* Divider */}
                    <div style={styles.divider}>
                        <div style={styles.dividerLine}></div>
                        <span style={styles.dividerText}>OR</span>
                        <div style={styles.dividerLine}></div>
                    </div>
                    
                    {/* Social Login Buttons */}
                    <div style={styles.socialButtons}>
                        <button
                            type="button"
