import React, { useState, useEffect } from 'react';
import { organizationProfileService, userService } from '../services/apiService';
import { useNavigate } from 'react-router-dom';
import '../style/orgPersonalisasiProfile.css';

const OrgPersonalisasiProfile = () => {
    const navigate = useNavigate();
    const [profile, setProfile] = useState({
        org_name: '',
        contact_person: '',
        contact_phone_org: '',
        org_type: 'retail' // default value
    });
    
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');

    useEffect(() => {
        const checkAuthAndLoadProfile = async () => {
            try {
                // Cek status login
                const isLoggedIn = await userService.isLoggedIn();
                if (!isLoggedIn) {
                    navigate('/login');
                    return;
                }

                // Cek apakah user adalah organisasi
                const isOrg = userService.isOrganization();
                if (!isOrg) {
                    navigate('/dashboard');
                    return;
                }

                // Load profile jika semua pengecekan berhasil
                await loadProfile();
            } catch (error) {
                console.error("Authentication error:", error);
                if (error.response && error.response.status === 401) {
                    navigate('/login');
                } else {
                    setError(error.message || 'Error saat memverifikasi pengguna');
                }
            } finally {
                setLoading(false);
            }
        };

        checkAuthAndLoadProfile();
    }, [navigate]);

    const loadProfile = async () => {
        try {
            setLoading(true);
            const response = await organizationProfileService.getOrgProfileById();
            
            if (response && response.data) {
                // Pastikan semua field terisi, gunakan data atau nilai default
                setProfile({
                    org_name: response.data.org_name || '',
                    contact_person: response.data.contact_person || '',
                    contact_phone_org: response.data.contact_phone_org || '',
                    org_type: response.data.org_type || 'retail'
                });
            }
        } catch (error) {
            console.error("Profile loading error:", error);
            setError('Gagal memuat profil organisasi: ' + (error.response?.data?.message || error.message || 'Terjadi kesalahan'));
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfile(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage('');
        
        try {
            // Validasi form
            if (!profile.org_name.trim()) {
                setError('Nama organisasi tidak boleh kosong');
                return;
            }
            
            if (!profile.contact_person.trim()) {
                setError('Contact person tidak boleh kosong');
                return;
            }
            
            if (!profile.contact_phone_org.trim()) {
                setError('Nomor telepon tidak boleh kosong');
                return;
            }

            // Cek status login sebelum submit
            const isLoggedIn = await userService.isLoggedIn();
            if (!isLoggedIn) {
                navigate('/login');
                return;
            }

            // Tampilkan loading state
            setLoading(true);
            
            const response = await organizationProfileService.createOrUpdateProfile(profile);
            
            if (response && response.status === 'success') {
                setSuccessMessage('Profil organisasi berhasil diperbarui!');
                
                // Reload profile setelah update untuk memastikan data terbaru
                await loadProfile();
                
                // Auto-clear success message after 5 seconds
                setTimeout(() => {
                    setSuccessMessage('');
                }, 5000);
            } else {
                throw new Error(response?.message || 'Gagal memperbarui profil');
            }
        } catch (error) {
            console.error("Profile update error:", error);
            if (error.response?.status === 401) {
                navigate('/login');
            } else {
                setError('Gagal memperbarui profil: ' + (error.response?.data?.message || error.message || 'Terjadi kesalahan'));
            }
        } finally {
            setLoading(false);
        }
    };

    const validatePhoneNumber = (e) => {
        // Hanya izinkan input berupa angka dan beberapa karakter khusus untuk format
        const value = e.target.value;
        // Izinkan: angka, tanda plus di awal, tanda kurung, spasi, dan tanda hubung
        if (!/^[0-9+() -]*$/.test(value)) {
            e.preventDefault();
            return;
        }
        handleChange(e);
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loader"></div>
                <div>Memuat data profil...</div>
            </div>
        );
    }

    return (
        <div className="org-profile-container">
            <h1 className="profile-title">
                Profil Organisasi
                <span className="title-decoration"></span>
            </h1>

            {error && (
                <div className="error-message">
                    <span className="error-icon">⚠️</span> {error}
                </div>
            )}
            
            {successMessage && (
                <div className="success-message">
                    <span className="success-icon">✅</span> {successMessage}
                </div>
            )}

            <form onSubmit={handleSubmit} className="profile-form">
                <div className="form-group">
                    <label className="form-label" htmlFor="org_name">Nama Organisasi</label>
                    <input
                        id="org_name"
                        type="text"
                        name="org_name"
                        value={profile.org_name}
                        onChange={handleChange}
                        className="form-input"
                        required
                        placeholder="Masukkan nama organisasi"
                    />
                </div>

                <div className="form-group">
                    <label className="form-label" htmlFor="contact_person">Contact Person</label>
                    <input
                        id="contact_person"
                        type="text"
                        name="contact_person"
                        value={profile.contact_person}
                        onChange={handleChange}
                        className="form-input"
                        required
                        placeholder="Masukkan nama contact person"
                    />
                </div>

                <div className="form-group">
                    <label className="form-label" htmlFor="contact_phone_org">Nomor Telepon Organisasi</label>
                    <input
                        id="contact_phone_org"
                        type="tel"
                        name="contact_phone_org"
                        value={profile.contact_phone_org}
                        onChange={validatePhoneNumber}
                        className="form-input"
                        required
                        placeholder="Contoh: 08123456789"
                    />
                </div>

                <div className="form-group">
                    <label className="form-label" htmlFor="org_type">Tipe Organisasi</label>
                    <select
                        id="org_type"
                        name="org_type"
                        value={profile.org_type}
                        onChange={handleChange}
                        className="form-select"
                        required
                    >
                        <option value="retail">Retail</option>
                        <option value="corporate">Corporate</option>
                        <option value="education">Education</option>
                        <option value="government">Government</option>
                        <option value="non_profit">Non Profit</option>
                    </select>
                </div>

                <div className="button-container">
                    <button
                        type="button"
                        onClick={() => navigate('/dashboard')}
                        className="back-button"
                        disabled={loading}
                    >
                        Kembali ke Dashboard
                    </button>
                    <button 
                        type="submit" 
                        className="save-button"
                        disabled={loading}
                    >
                        {loading ? 'Menyimpan...' : 'Simpan Profil'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default OrgPersonalisasiProfile;
