import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// Components
import SignLogin from './components/signLogin';
import Dashboard from './components/dashboard';
import CustomerProfile from './components/CustomerProfile';
import { userService } from './services/apiService';

// Protected Route Component
const ProtectedRoute = ({ element, allowedRoles }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [userRole, setUserRole] = useState(null);

    useEffect(() => {
        const checkAuth = async () => {
            try {
                const isLoggedIn = await userService.isLoggedIn();
                if (isLoggedIn) {
                    const user = await userService.getCurrentUser();
                    setUserRole(user?.role);
                }
                setIsAuthenticated(isLoggedIn);
            } catch (error) {
                setIsAuthenticated(false);
            } finally {
                setIsLoading(false);
            }
        };
        checkAuth();
    }, []);

    if (isLoading) {
        return <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</div>;
    }

    if (!isAuthenticated) {
        return <Navigate to={`/${userRole}-dashboard`} />;
    }

    if (allowedRoles && !allowedRoles.includes(userRole)) {
        return <Navigate to={`/${userRole}-dashboard`} />;
    }

    return element;
};

// Customer Route Component
const CustomerRoute = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isCustomer, setIsCustomer] = useState(false);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const checkAuth = async () => {
            try {
                const loggedIn = await userService.isLoggedIn();
                const isUserCustomer = await userService.isCustomer();
                setIsAuthenticated(loggedIn);
                setIsCustomer(isUserCustomer);
            } catch (error) {
                setIsAuthenticated(false);
                setIsCustomer(false);
            } finally {
                setIsLoading(false);
            }
        };
        checkAuth();
    }, []);

    if (isLoading) {
        return <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</div>;
    }

    if (!isAuthenticated) {
        return <Navigate to={`/${userRole}-dashboard`}/>;
    }

    if (!isCustomer) {
        return <Navigate to={`/${userRole}-dashboard`} />;
    }

    return children;
};

// Auth Route Component
const AuthRoute = ({ children }) => {
    const location = useLocation();
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const isLogin = location.pathname === '/login';

    useEffect(() => {
        const checkAuth = async () => {
            try {
                const loggedIn = await userService.isLoggedIn();
                setIsAuthenticated(loggedIn);
            } catch (error) {
                setIsAuthenticated(false);
            } finally {
                setIsLoading(false);
            }
        };
        checkAuth();
    }, []);

    if (isLoading) {
        return <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</div>;
    }

    if (isAuthenticated) {
        return <Navigate to={`/${userRole}-dashboard`} />;
    }

    return React.cloneElement(children, { isLogin });
};

function App() {
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const checkAuthStatus = async () => {
            try {
                const isLoggedIn = await userService.isLoggedIn();
                if (isLoggedIn) {
                    await userService.getProfile();
                }
            } catch (error) {
                await userService.logout();
            } finally {
                setLoading(false);
            }
        };

        checkAuthStatus();
    }, []);

    if (loading) {
        return <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>Loading...</div>;
    }

    return (
        <Router>
            <ToastContainer position="top-right" autoClose={3000} />
            <Routes>
                {/* Auth routes */}
                <Route path="/login" element={<AuthRoute><SignLogin /></AuthRoute>} />
                <Route path="/register" element={<AuthRoute><SignLogin /></AuthRoute>} />
                
                {/* Role-specific dashboard routes */}
                <Route path="/admin-dashboard" element={
                    <ProtectedRoute 
                        element={<Dashboard />} 
                        allowedRoles={['admin']} 
                    />
                } />
                <Route path="/organization-dashboard" element={
                    <ProtectedRoute 
                        element={<Dashboard />} 
                        allowedRoles={['organization']} 
                    />
                } />
                <Route path="/customer-dashboard" element={
                    <ProtectedRoute 
                        element={<Dashboard />} 
                        allowedRoles={['customer']} 
                    />
                } />
                
                {/* Customer profile route */}
                <Route path="/profile" element={
                    <ProtectedRoute
                        element={<CustomerRoute><CustomerProfile /></CustomerRoute>}
                        allowedRoles={['customer']} 
                    />
                } />
                
                {/* Default routes */}
                <Route path="/" element={<Navigate to="/login" />} />
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
