import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API, {userService} from '../services/api';
import { toast } from 'react-toastify';

const SignLogin = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    
    // Toggle antara form login dan register
    const toggleForm = () => {
        setIsLogin(!isLogin);
        // Reset form
        setEmail('');
        setPassword('');
        setConfirmPassword('');
    };
    
    // Handle submit form
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        
        try {
            if (isLogin) {
                // Handle login
                const result = await userServices.login(email, password);
                toast.success(result.message || 'Login berhasil');
                navigate('/dashboard');
            } else {
                // Handle register
                // Validasi password
                if (password !== confirmPassword) {
                    toast.error('Password dan konfirmasi password tidak cocok');
                    setLoading(false);
                    return;
                }
                
                const result = await userServices.register(email, password);
                toast.success(result.message || 'Registrasi berhasil');
                // Setelah register, arahkan ke login
                setIsLogin(true);
                // Reset form
                setEmail('');
                setPassword('');
                setConfirmPassword('');
            }
        } catch (error) {
            toast.error(error.message || 'Terjadi kesalahan');
            console.error('Auth error:', error);
        } finally {
            setLoading(false);
        }
    };
    
    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="w-full max-w-md bg-white rounded-lg shadow-md p-8">
                <h2 className="text-2xl font-bold text-center mb-6">
                    {isLogin ? 'Login to Your Account' : 'Create an Account'}
                </h2>
                
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label htmlFor="email" className="block text-gray-700 font-medium mb-2">
                            Email
                        </label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            required
                        />
                    </div>
                    
                    <div className="mb-4">
                        <label htmlFor="password" className="block text-gray-700 font-medium mb-2">
                            Password
                        </label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            required
                        />
                    </div>
                    
                    {!isLogin && (
                        <div className="mb-4">
                            <label htmlFor="confirmPassword" className="block text-gray-700 font-medium mb-2">
                                Confirm Password
                            </label>
                            <input
                                type="password"
                                id="confirmPassword"
                                value={confirmPassword}
                                onChange={(e) => setConfirmPassword(e.target.value)}
                                className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                required
                            />
                        </div>
                    )}
                    
                    <button
                        type="submit"
                        className={`w-full py-2 px-4 rounded-lg text-white font-medium ${
                            loading ? 'bg-gray-500' : 'bg-blue-600 hover:bg-blue-700'
                        }`}
                        disabled={loading}
                    >
                        {loading ? 'Processing...' : isLogin ? 'Login' : 'Register'}
                    </button>
                </form>
                
                <div className="mt-6 text-center">
                    <p className="text-gray-600">
                        {isLogin ? "Don't have an account? " : 'Already have an account? '}
                        <button
                            onClick={toggleForm}
                            className="text-blue-600 hover:underline font-medium"
                        >
                            {isLogin ? 'Register' : 'Login'}
                        </button>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default SignLogin;
