import React, { useState, useEffect,useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {userService, productService} from '../services/apiService';
import { toast } from 'react-toastify';

const Dashboard = () => {
    const [products] = useState([]);
    const [user, setUser] = useState(null);
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    
 
    const handleLogout = useCallback(() => {
        userService.logout();
        navigate('/');
    }, [navigate]);
    // Mendapatkan profil user saat komponen dimuat
    useEffect(() => {
        // Handle logout
        
        const loadProfile = async () => {
            try {
                const result = await userService.getProfile();
                setUser(result.data);
                
                // Jika user adalah admin, ambil semua data users
                if (userService.isAdmin()) {
                    const usersResult = await userService.getAllUsers();
                    setUsers(usersResult.data || []);
                }
            } catch (error) {
                toast.error(error.message || 'Gagal memuat profil');
                if (error.status === 401) {
                    handleLogout();
                }
            } finally {
                setLoading(false);
            }
        };

        const fetchProducts = async () => {
            try {
                const response = await productService.getAllProducts();
                products(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };
        

        loadProfile();
        fetchProducts();
    }, [handleLogout]);
    
    if (error) return <div>Error: {error}</div>;
    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-xl font-medium">Loading...</div>
            </div>
            
        );
    }
    
    
    return (
        <div className="min-h-screen bg-gray-100">
            <header className="bg-white shadow-sm">
                <div className="max-w-7xl mx-auto py-4 px-6">
                    <div className="flex justify-between items-center">
                        <h1 className="text-2xl font-bold text-gray-800">Bottled Water Delivery</h1>
                        <div className="flex items-center">
                            <span className="mr-4 text-gray-600">
                                {user?.email} ({user?.role})
                            </span>
                            <button
                                onClick={handleLogout}
                                className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700"
                            >
                                Logout
                            </button>
                        </div>
                    </div>
                </div>
            </header>
            
            <main className="max-w-7xl mx-auto py-6 px-6">
                <div className="bg-white shadow-md rounded-lg p-6 mb-6">
                    <h2 className="text-xl font-semibold mb-4">Selamat Datang!</h2>
                    <p className="text-gray-600">
                        Anda telah masuk sebagai <span className="font-medium">{user?.role}</span>.
                    </p>
                </div>
                {userService.isCustomer()&& (
                    <div className="container mx-auto p-4">
                        <h1 className="text-2xl font-bold mb-4">Products Dashboard</h1>
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                            {products.map((product) => (
                                <div key={product.id} className="border rounded-lg p-4 shadow-sm">
                                    <img src={product.image_url || 'default-image.jpg'} alt={product.name} className="w-full h-48 object-cover rounded-md mb-2"/>
                                    <h2 className="text-xl font-semibold">{product.name}</h2>
                                    <p className="text-gray-600">{product.description}</p>
                                    <p className="text-lg font-bold mt-2">Rp {product.price}</p>
                                    <button className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600" onClick={() => {/* Add to cart logic here */}}>
                                        Add to Cart
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                )}
                {/* Admin Panel - hanya muncul jika user adalah admin */}
                {userService.isAdmin() && (
                    <div className="bg-white shadow-md rounded-lg p-6">
                        <h2 className="text-xl font-semibold mb-4">Admin Panel</h2>
                        
                        <div className="mt-4">
                            <h3 className="text-lg font-medium mb-2">Daftar Pengguna</h3>
                            {users.length > 0 ? (
                                <div className="overflow-x-auto">
                                    <table className="min-w-full bg-white">
                                        <thead>
                                            <tr>
                                                <th className="py-2 px-4 border-b border-gray-200 text-left text-sm font-medium text-gray-700">ID</th>
                                                <th className="py-2 px-4 border-b border-gray-200 text-left text-sm font-medium text-gray-700">Email</th>
                                                <th className="py-2 px-4 border-b border-gray-200 text-left text-sm font-medium text-gray-700">Role</th>
                                                <th className="py-2 px-4 border-b border-gray-200 text-left text-sm font-medium text-gray-700">Status</th>
                                                <th className="py-2 px-4 border-b border-gray-200 text-left text-sm font-medium text-gray-700">Tanggal Dibuat</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {users.map((user) => (
                                                <tr key={user.user_id}>
                                                    <td className="py-2 px-4 border-b border-gray-200 text-sm text-gray-500">{user.user_id}</td>
                                                    <td className="py-2 px-4 border-b border-gray-200 text-sm text-gray-500">{user.email}</td>
                                                    <td className="py-2 px-4 border-b border-gray-200 text-sm text-gray-500">{user.role}</td>
                                                    <td className="py-2 px-4 border-b border-gray-200 text-sm">
                                                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${user.is_active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                                                            {user.is_active ? 'Active' : 'Inactive'}
                                                        </span>
                                                    </td>
                                                    <td className="py-2 px-4 border-b border-gray-200 text-sm text-gray-500">
                                                        {new Date(user.created_at).toLocaleDateString()}
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            ) : (
                                <p className="text-gray-500">Tidak ada data pengguna.</p>
                            )}
                        </div>
                    </div>
                )}
                
                {/* Organization Panel - hanya muncul jika user adalah organization */}
                {userService.isOrganization() && (
                    <div className="bg-white shadow-md rounded-lg p-6">
                        <h2 className="text-xl font-semibold mb-4">Organization Dashboard</h2>
                        <p className="text-gray-600">
                            Tempat untuk mengelola pesanan air minum dan konfigurasi organisasi.
                        </p>
                        {/* Tambahkan konten dashboard untuk organization */}
                    </div>
                )}
                
                {/* Customer Panel - hanya muncul jika user adalah customer */}
                {userService.isCustomer() && (
                    <div className="bg-white shadow-md rounded-lg p-6">
                        <h2 className="text-xl font-semibold mb-4">Customer Dashboard</h2>
                        <p className="text-gray-600">
                            Tempat untuk memesan air minum dan melihat riwayat pesanan Anda.
                        </p>
                        {/* Tambahkan konten dashboard untuk customer */}
                    </div>
                )}
            </main>
        </div>
    );
};

export default Dashboard;
