import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { orderService, productService, userService } from '../services/apiService';
import '../style/orderPage.css';

const OrderPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [orders, setOrders] = useState([]);
    const [products, setProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState('');
    const [quantity, setQuantity] = useState(1);
    const [paymentMethod, setPaymentMethod] = useState('cash');
    const [notes, setNotes] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const user = userService.getCurrentUser();
                if (!user) {
                    navigate('/registration-or-login');
                    return;
                }

                const [productsData, ordersData] = await Promise.all([
                    productService.getAllProducts(),
                    orderService.getCustomerOrders(user.sub) // Using sub from JWT token which contains user ID
                ]);
                
                setProducts(productsData.data);
                setOrders(ordersData.data);

                // Set selected product if passed from dashboard
                if (location.state?.selectedProduct) {
                    setSelectedProduct(location.state.selectedProduct.product_id.toString());
                }
                
                setLoading(false);
            } catch (err) {
                console.error('Error fetching data:', err);
                setError('Failed to load data. Please try again.');
                setLoading(false);
            }
        };

        fetchData();
    }, [location.state, navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const user = userService.getCurrentUser();
            if (!user) {
                navigate('/registration-or-login');
                return;
            }

            const product = products.find(p => p.product_id.toString() === selectedProduct);
            const orderData = {
                customer_id: user.sub, // Using sub from JWT token which contains user ID
                product_id: parseInt(selectedProduct),
                quantity: quantity,
                total_amount: product.unit_price * quantity,
                payment_method: paymentMethod,
                notes: notes
            };

            await orderService.createOrder(orderData);
            // Refresh orders list
            const updatedOrders = await orderService.getCustomerOrders(user.sub);
            setOrders(updatedOrders.data);
            
            // Reset form
            setSelectedProduct('');
            setQuantity(1);
            setNotes('');
            setError(null);
        } catch (err) {
            console.error('Error creating order:', err);
            setError('Failed to create order. Please try again.');
        }
    };

    const calculateTotal = () => {
        if (!selectedProduct) return 0;
        const product = products.find(p => p.product_id.toString() === selectedProduct);
        return product ? product.unit_price * quantity : 0;
    };

    if (loading) return <div className="loading">Loading...</div>;
    if (error) return <div className="error-message">{error}</div>;

    return (
        <div className="order-page">
            <h2>Place New Order</h2>
            <form onSubmit={handleSubmit} className="order-form">
                <div className="form-group">
                    <label>Select Product:</label>
                    <select 
                        value={selectedProduct}
                        onChange={(e) => setSelectedProduct(e.target.value)}
                        required
                    >
                        <option value="">Select a product</option>
                        {products.map(product => (
                            <option key={product.product_id} value={product.product_id}>
                                {product.container_type} - Rp {product.unit_price}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="form-group">
                    <label>Quantity:</label>
                    <input
                        type="number"
                        min="1"
                        value={quantity}
                        onChange={(e) => setQuantity(parseInt(e.target.value) || 1)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Total Amount:</label>
                    <div className="total-amount">
                        Rp {calculateTotal()}
                    </div>
                </div>

                <div className="form-group">
                    <label>Payment Method:</label>
                    <select
                        value={paymentMethod}
                        onChange={(e) => setPaymentMethod(e.target.value)}
                        required
                    >
                        <option value="cash">Cash</option>
                        <option value="transfer">Bank Transfer</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>Notes:</label>
                    <textarea
                        value={notes}
                        onChange={(e) => setNotes(e.target.value)}
                        placeholder="Add any special instructions here"
                    />
                </div>

                <button type="submit" className="submit-button">Place Order</button>
            </form>

            <h2>Your Orders</h2>
            <div className="orders-list">
                {orders.length === 0 ? (
                    <p>No orders found</p>
                ) : (
                    orders.map(order => (
                        <div key={order.order_id} className="order-card">
                            <div className="order-header">
                                <span>Order #{order.order_id}</span>
                                <span className={`status ${order.status}`}>{order.status}</span>
                            </div>
                            <div className="order-details">
                                <p>Date: {new Date(order.order_date).toLocaleDateString()}</p>
                                <p>Delivery Date: {order.delivery_date ? new Date(order.delivery_date).toLocaleDateString() : 'Not set'}</p>
                                <p>Total Amount: Rp {order.total_amount}</p>
                                <p>Payment Method: {order.payment_method}</p>
                                <p>Payment Status: {order.payment_status}</p>
                                {order.notes && <p>Notes: {order.notes}</p>}
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default OrderPage;
