import React, { useState, useEffect } from 'react';
import { customerProfileService, userService } from '../services/apiService';
import { useNavigate } from 'react-router-dom';

const CustomerProfile = () => {
    const navigate = useNavigate();
    const [profile, setProfile] = useState({
        full_name: '',
        phone: '',
        address: {
            negara: '',
            provinsi: '',
            kota: '',
            kabupaten: '',
            kelurahan: '',
            jalan: '',
            kode_pos: ''
        },
        delivery_instructions: JSON.stringify({
            preferred_time: '09:00',
            day_preference: 'weekday'
        })
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');

    useEffect(() => {
        const checkAuthAndLoadProfile = async () => {
            try {
                const isLoggedIn = await userService.isLoggedIn();
                const isCustomer = await userService.isCustomer();
                
                if (!isLoggedIn || !isCustomer) {
                    navigate('/login');
                    return;
                }

                await loadProfile();
            } catch (error) {
                if (error.response?.status === 401) {
                    navigate('/login');
                } else {
                    setError(error.message || 'Error loading profile');
                }
            } finally {
                setLoading(false);
            }
        };

        checkAuthAndLoadProfile();
    }, [navigate]);

    const loadProfile = async () => {
        try {
            const response = await customerProfileService.getCustomerProfileById();
            if (response.data) {
                const loadedProfile = response.data;
                try {
                    const parsedInstructions = JSON.parse(loadedProfile.delivery_instructions);
                    const address = typeof loadedProfile.address === 'string' 
                        ? JSON.parse(loadedProfile.address)
                        : loadedProfile.address || {
                            negara: '',
                            provinsi: '',
                            kota: '',
                            kabupaten: '',
                            kelurahan: '',
                            jalan: '',
                            kode_pos: ''
                        };
                    
                    setProfile({
                        ...loadedProfile,
                        address,
                        delivery_instructions: JSON.stringify(parsedInstructions)
                    });
                } catch (e) {
                    setProfile({
                        ...loadedProfile,
                        address: {
                            negara: '',
                            provinsi: '',
                            kota: '',
                            kabupaten: '',
                            kelurahan: '',
                            jalan: '',
                            kode_pos: ''
                        },
                        delivery_instructions: JSON.stringify({
                            preferred_time: '09:00',
                            day_preference: 'weekday'
                        })
                    });
                }
            }
        } catch (error) {
            setError(error.message || 'Error loading profile');
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'preferred_time' || name === 'day_preference') {
            const currentInstructions = JSON.parse(profile.delivery_instructions);
            setProfile(prev => ({
                ...prev,
                delivery_instructions: JSON.stringify({
                    ...currentInstructions,
                    [name]: value
                })
            }));
        } else if (name.startsWith('address.')) {
            const addressField = name.split('.')[1];
            setProfile(prev => ({
                ...prev,
                address: {
                    ...prev.address,
                    [addressField]: value
                }
            }));
        } else {
            setProfile(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage('');
        try {
            const isLoggedIn = await userService.isLoggedIn();
            if (!isLoggedIn) {
                throw new Error('Silakan login terlebih dahulu');
            }

            const existingProfile = await customerProfileService.getCustomerProfileById();
            
            if (existingProfile.data) {
                await customerProfileService.updateCustomerProfile(
                    profile.full_name,
                    profile.phone,
                    profile.address,
                    profile.delivery_instructions
                );
            } else {
                await customerProfileService.createCustomerProfile(
                    profile.full_name,
                    profile.phone,
                    profile.address,
                    profile.delivery_instructions
                );
            }
            
            setSuccessMessage('Profile berhasil diperbarui!');
            await loadProfile(); // Reload profile to get updated data
        } catch (error) {
            if (error.response?.status === 401) {
                navigate('/login');
            } else {
                setError(error.message || 'Error updating profile');
            }
        }
    };

    const deliveryTimes = [
        '09:00', '10:00', '11:00', '13:00', '14:00', '15:00', '16:00'
    ];

    const styles = {
        container: {
            maxWidth: '720px',
            margin: '60px auto',
            padding: '0 24px',
            fontFamily: "'Poppins', sans-serif",
            color: '#2c3e50',
        },
        title: {
            fontSize: '32px',
            fontWeight: '700',
            textAlign: 'center',
            marginBottom: '40px',
            position: 'relative',
            color: '#34495e',
        },
        titleAfter: {
            content: '""',
            position: 'absolute',
            width: '60px',
            height: '4px',
            backgroundColor: '#00b894',
            left: '50%',
            bottom: '-10px',
            transform: 'translateX(-50%)',
            borderRadius: '4px'
        },
        form: {
            backgroundColor: '#ffffff',
            padding: '40px',
            borderRadius: '16px',
            boxShadow: '0 12px 24px rgba(0,0,0,0.08)',
        },
        formGroup: {
            marginBottom: '24px',
        },
        label: {
            display: 'block',
            marginBottom: '10px',
            fontWeight: '600',
            fontSize: '16px',
            color: '#2d3436'
        },
        input: {
            width: '100%',
            padding: '12px 16px',
            fontSize: '15px',
            borderRadius: '10px',
            border: '1px solid #dfe6e9',
            transition: 'all 0.3s ease',
            boxSizing: 'border-box'
        },
        inputFocus: {
            borderColor: '#0984e3',
            boxShadow: '0 0 0 4px rgba(9, 132, 227, 0.1)',
            outline: 'none'
        },
        textarea: {
            width: '100%',
            padding: '14px 18px',
            fontSize: '15px',
            borderRadius: '10px',
            border: '1px solid #dfe6e9',
            minHeight: '120px',
            resize: 'vertical',
            transition: 'all 0.3s ease'
        },
        button: {
            backgroundColor: '#00cec9',
            color: '#ffffff',
            padding: '14px 24px',
            border: 'none',
            borderRadius: '10px',
            fontSize: '16px',
            fontWeight: '600',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            width: '100%',
            marginTop: '20px',
            boxShadow: '0 6px 12px rgba(0,0,0,0.1)'
        },
        buttonHover: {
            backgroundColor: '#00b894',
            transform: 'translateY(-2px)',
            boxShadow: '0 10px 18px rgba(0,0,0,0.08)'
        },
        buttonActive: {
            transform: 'translateY(0)'
        },
        error: {
            backgroundColor: '#ffe6e6',
            color: '#e74c3c',
            padding: '12px 18px',
            borderRadius: '8px',
            fontSize: '14px',
            marginTop: '20px',
            borderLeft: '4px solid #e74c3c'
        },
        success: {
            backgroundColor: '#eafaf1',
            color: '#27ae60',
            padding: '12px 18px',
            borderRadius: '8px',
            fontSize: '14px',
            marginTop: '20px',
            borderLeft: '4px solid #27ae60'
        },
        timeSelect: {
            width: '100%',
            padding: '12px 16px',
            borderRadius: '10px',
            border: '1px solid #dfe6e9',
            fontSize: '15px',
            appearance: 'none',
            backgroundColor: '#fff',
            backgroundImage: 'url("data:image/svg+xml;utf8,<svg fill=\'%23333\' height=\'24\' viewBox=\'0 0 24 24\' width=\'24\' xmlns=\'http://www.w3.org/2000/svg\'><path d=\'M7 10l5 5 5-5z\'/></svg>")',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'right 12px center',
            backgroundSize: '18px',
            transition: 'all 0.3s ease',
        },
        radioGroup: {
            display: 'flex',
            gap: '20px',
            flexWrap: 'wrap',
            marginTop: '10px'
        },
        radioLabel: {
            display: 'flex',
            alignItems: 'center',
            fontWeight: '500',
            cursor: 'pointer',
            position: 'relative',
            paddingLeft: '28px',
            fontSize: '15px',
            color: '#2d3436'
        },
        radioInput: {
            position: 'absolute',
            opacity: '0',
            cursor: 'pointer'
        },
        radioCustom: {
            position: 'absolute',
            left: '0',
            top: '2px',
            width: '18px',
            height: '18px',
            borderRadius: '50%',
            border: '2px solid #b2bec3',
            backgroundColor: '#fff',
            transition: 'all 0.2s ease'
        },
        radioInputChecked: {
            borderColor: '#00b894',
            backgroundColor: '#00b894'
        },
        loading: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '200px',
            fontSize: '18px',
            color: '#636e72'
        }
    };
    

    const deliveryInstructions = JSON.parse(profile.delivery_instructions);

    if (loading) {
        return (
            <div style={styles.loading}>
                <div>Loading profile data...</div>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h1 style={styles.title}>
                Personalisasi Profil
                <span style={styles.titleAfter}></span>
            </h1>

            {error && <div style={styles.error}>{error}</div>}
            {successMessage && <div style={styles.success}>{successMessage}</div>}

            <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.formGroup}>
                    <label style={styles.label}>Nama Lengkap</label>
                    <input
                        type="text"
                        name="full_name"
                        value={profile.full_name}
                        onChange={handleChange}
                        style={styles.input}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
                        onBlur={(e) => e.target.style = styles.input}
                        required
                    />
                </div>

                <div style={styles.formGroup}>
                    <label style={styles.label}>Nomor Telepon</label>
                    <input
                        type="tel"
                        name="phone"
                        value={profile.phone}
                        onChange={handleChange}
                        style={styles.input}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
                        onBlur={(e) => e.target.style = styles.input}
                        required
                    />
                </div>

                <div style={styles.formGroup}>
                    <label style={styles.label}>Alamat</label>
                    <input
                        type="text"
                        name="address.negara"
                        value={profile.address.negara}
                        onChange={handleChange}
                        placeholder="Negara"
                        style={styles.input}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
                        onBlur={(e) => e.target.style = styles.input}
                        required
                    />
                    <input
                        type="text"
                        name="address.provinsi"
                        value={profile.address.provinsi}
                        onChange={handleChange}
                        placeholder="Provinsi"
                        style={{...styles.input, marginTop: '10px'}}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus, marginTop: '10px' }}
                        onBlur={(e) => e.target.style = {...styles.input, marginTop: '10px'}}
                        required
                    />
                    <input
                        type="text"
                        name="address.kota"
                        value={profile.address.kota}
                        onChange={handleChange}
                        placeholder="Kota"
                        style={{...styles.input, marginTop: '10px'}}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus, marginTop: '10px' }}
                        onBlur={(e) => e.target.style = {...styles.input, marginTop: '10px'}}
                        required
                    />
                    <input
                        type="text"
                        name="address.kabupaten"
                        value={profile.address.kabupaten}
                        onChange={handleChange}
                        placeholder="Kabupaten"
                        style={{...styles.input, marginTop: '10px'}}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus, marginTop: '10px' }}
                        onBlur={(e) => e.target.style = {...styles.input, marginTop: '10px'}}
                        required
                    />
                    <input
                        type="text"
                        name="address.kelurahan"
                        value={profile.address.kelurahan}
                        onChange={handleChange}
                        placeholder="Kelurahan"
                        style={{...styles.input, marginTop: '10px'}}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus, marginTop: '10px' }}
                        onBlur={(e) => e.target.style = {...styles.input, marginTop: '10px'}}
                        required
                    />
                    <input
                        type="text"
                        name="address.jalan"
                        value={profile.address.jalan}
                        onChange={handleChange}
                        placeholder="Nama Jalan"
                        style={{...styles.input, marginTop: '10px'}}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus, marginTop: '10px' }}
                        onBlur={(e) => e.target.style = {...styles.input, marginTop: '10px'}}
                        required
                    />

                    <input
                        type="text"
                        name="address.kode_pos"
                        value={profile.address.kode_pos}
                        onChange={handleChange}
                        placeholder="Kode Pos"
                        style={{...styles.input, marginTop: '10px'}}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus, marginTop: '10px' }}
                        onBlur={(e) => e.target.style = {...styles.input, marginTop: '10px'}}
                        required
                    />
                </div>

                <div style={styles.formGroup}>
                    <label style={styles.label}>Waktu Pengiriman yang Diinginkan</label>
                    <select
                        name="preferred_time"
                        value={deliveryInstructions.preferred_time}
                        onChange={handleChange}
                        style={styles.timeSelect}
                        onFocus={(e) => e.target.style = { ...styles.timeSelect, ...styles.inputFocus }}
                        onBlur={(e) => e.target.style = styles.timeSelect}
                    >
                        {deliveryTimes.map(time => (
                            <option key={time} value={time}>
                                {time} WIB
                            </option>
                        ))}
                    </select>

                    <div style={styles.radioGroup}>
                        <label style={styles.radioLabel}>
                            <input
                                type="radio"
                                name="day_preference"
                                value="weekday"
                                checked={deliveryInstructions.day_preference === 'weekday'}
                                onChange={handleChange}
                                style={styles.radioInput}
                            />
                            <span 
                                style={{
                                    ...styles.radioCustom,
                                    ...(deliveryInstructions.day_preference === 'weekday' ? styles.radioInputChecked : {})
                                }}
                            ></span>
                            Hari Kerja (Senin-Jumat)
                        </label>
                        <label style={styles.radioLabel}>
                            <input
                                type="radio"
                                name="day_preference"
                                value="weekend"
                                checked={deliveryInstructions.day_preference === 'weekend'}
                                onChange={handleChange}
                                style={styles.radioInput}
                            />
                            <span 
                                style={{
                                    ...styles.radioCustom,
                                    ...(deliveryInstructions.day_preference === 'weekend' ? styles.radioInputChecked : {})
                                }}
                            ></span>
                            Akhir Pekan (Sabtu-Minggu)
                        </label>
                    </div>
                </div>

                <button 
                    type="submit" 
                    style={styles.button}
                    onMouseEnter={(e) => e.target.style = { ...styles.button, ...styles.buttonHover }}
                    onMouseLeave={(e) => e.target.style = styles.button}
                    onMouseDown={(e) => e.target.style = { ...styles.button, ...styles.buttonHover, ...styles.buttonActive }}
                    onMouseUp={(e) => e.target.style = { ...styles.button, ...styles.buttonHover }}
                >
                    Simpan Profil
                </button>

                {error && <div style={styles.error}>{error}</div>}
                {successMessage && <div style={styles.success}>{successMessage}</div>}
            </form>
        </div>
    );
};

export default CustomerProfile;
