import React, { useState, useEffect } from 'react';
import { customerProfileService, userService } from '../services/apiService';

const CustomerProfile = () => {
    const [profile, setProfile] = useState({
        full_name: '',
        phone: '',
        address: '',
        delivery_instructions: JSON.stringify({
            preferred_time: '09:00',
            day_preference: 'weekday'
        })
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');

    useEffect(() => {
        loadProfile();
    }, []);

    const loadProfile = async () => {
        try {
            const currentUser = userService.getCurrentUser();
            if (currentUser) {
                const response = await customerProfileService.getCustomerProfileById(currentUser.user_id);
                if (response.data) {
                    const loadedProfile = response.data;
                    try {
                        // Attempt to parse existing delivery instructions
                        JSON.parse(loadedProfile.delivery_instructions);
                        setProfile(loadedProfile);
                    } catch {
                        // If parsing fails, it's old format, so convert to new format
                        setProfile({
                            ...loadedProfile,
                            delivery_instructions: JSON.stringify({
                                preferred_time: '09:00',
                                day_preference: 'weekday'
                            })
                        });
                    }
                }
            }
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'preferred_time' || name === 'day_preference') {
            const currentInstructions = JSON.parse(profile.delivery_instructions);
            setProfile(prev => ({
                ...prev,
                delivery_instructions: JSON.stringify({
                    ...currentInstructions,
                    [name]: value
                })
            }));
        } else {
            setProfile(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage('');
        try {
            const currentUser = userService.getCurrentUser();
            await customerProfileService.createCustomerProfile(
                currentUser.user_id,
                profile.full_name,
                profile.phone,
                profile.address,
                profile.delivery_instructions
            );
            setSuccessMessage('Profile berhasil diperbarui!');
        } catch (error) {
            setError(error.message);
        }
    };

    const deliveryTimes = [
        '09:00', '10:00', '11:00', '13:00', '14:00', '15:00', '16:00'
    ];

    // Enhanced CSS styles
    const styles = {
        container: {
            maxWidth: '800px',
            margin: '40px auto',
            padding: '0 20px',
            fontFamily: "'Segoe UI', 'Roboto', 'Oxygen', sans-serif",
            color: '#333'
        },
        title: {
            fontSize: '28px',
            fontWeight: '600',
            marginBottom: '30px',
            color: '#2c3e50',
            textAlign: 'center',
            position: 'relative',
            paddingBottom: '15px'
        },
        titleAfter: {
            content: '""',
            position: 'absolute',
            bottom: '0',
            left: '50%',
            transform: 'translateX(-50%)',
            width: '80px',
            height: '4px',
            background: 'linear-gradient(90deg, #3498db, #2ecc71)',
            borderRadius: '2px'
        },
        form: {
            backgroundColor: '#fff',
            padding: '30px',
            borderRadius: '12px',
            boxShadow: '0 5px 15px rgba(0, 0, 0, 0.1)',
            transition: 'all 0.3s ease'
        },
        formGroup: {
            marginBottom: '25px',
            position: 'relative'
        },
        label: {
            display: 'block',
            marginBottom: '10px',
            fontWeight: '500',
            color: '#34495e',
            fontSize: '15px'
        },
        input: {
            width: '100%',
            padding: '12px 15px',
            border: '1px solid #ddd',
            borderRadius: '8px',
            fontSize: '15px',
            transition: 'all 0.3s ease',
            boxSizing: 'border-box'
        },
        inputFocus: {
            borderColor: '#3498db',
            boxShadow: '0 0 0 3px rgba(52, 152, 219, 0.2)',
            outline: 'none'
        },
        textarea: {
            width: '100%',
            padding: '12px 15px',
            border: '1px solid #ddd',
            borderRadius: '8px',
            fontSize: '15px',
            minHeight: '120px',
            resize: 'vertical',
            transition: 'all 0.3s ease'
        },
        button: {
            backgroundColor: '#3498db',
            color: 'white',
            padding: '14px 28px',
            border: 'none',
            borderRadius: '8px',
            fontSize: '16px',
            fontWeight: '500',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
            display: 'block',
            width: '100%',
            marginTop: '20px',
            boxShadow: '0 4px 6px rgba(50, 50, 93, 0.11)'
        },
        buttonHover: {
            backgroundColor: '#2980b9',
            transform: 'translateY(-2px)',
            boxShadow: '0 7px 14px rgba(50, 50, 93, 0.1)'
        },
        buttonActive: {
            transform: 'translateY(0)'
        },
        error: {
            color: '#e74c3c',
            marginTop: '10px',
            fontSize: '14px',
            padding: '10px',
            backgroundColor: '#fadbd8',
            borderRadius: '6px',
            borderLeft: '4px solid #e74c3c'
        },
        success: {
            color: '#27ae60',
            marginTop: '10px',
            fontSize: '14px',
            padding: '10px',
            backgroundColor: '#d5f5e3',
            borderRadius: '6px',
            borderLeft: '4px solid #27ae60'
        },
        timeSelect: {
            width: '100%',
            padding: '12px 15px',
            border: '1px solid #ddd',
            borderRadius: '8px',
            fontSize: '15px',
            marginBottom: '15px',
            appearance: 'none',
            backgroundImage: 'url("data:image/svg+xml;charset=US-ASCII,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%22292.4%22%20height%3D%22292.4%22%3E%3Cpath%20fill%3D%22%23333%22%20d%3D%22M287%2069.4a17.6%2017.6%200%200%200-13-5.4H18.4c-5%200-9.3%201.8-12.9%205.4A17.6%2017.6%200%200%200%200%2082.2c0%205%201.8%209.3%205.4%2012.9l128%20127.9c3.6%203.6%207.8%205.4%2012.8%205.4s9.2-1.8%2012.8-5.4L287%2095c3.5-3.5%205.4-7.8%205.4-12.8%200-5-1.9-9.2-5.5-12.8z%22%2F%3E%3C%2Fsvg%3E")',
            backgroundRepeat: 'no-repeat',
            backgroundPosition: 'right 10px center',
            backgroundSize: '12px auto',
            transition: 'all 0.3s ease'
        },
        radioGroup: {
            display: 'flex',
            gap: '20px',
            marginBottom: '15px',
            flexWrap: 'wrap'
        },
        radioLabel: {
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            cursor: 'pointer',
            position: 'relative',
            paddingLeft: '28px'
        },
        radioInput: {
            position: 'absolute',
            opacity: '0',
            cursor: 'pointer'
        },
        radioCustom: {
            position: 'absolute',
            top: '0',
            left: '0',
            height: '20px',
            width: '20px',
            backgroundColor: '#fff',
            border: '2px solid #bdc3c7',
            borderRadius: '50%',
            transition: 'all 0.3s ease'
        },
        radioInputChecked: {
            backgroundColor: '#3498db',
            borderColor: '#3498db'
        },
        radioCustomAfter: {
            content: '""',
            position: 'absolute',
            display: 'none',
            top: '4px',
            left: '4px',
            width: '12px',
            height: '12px',
            borderRadius: '50%',
            background: 'white'
        },
        loading: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '200px',
            fontSize: '18px',
            color: '#7f8c8d'
        }
    };

    const deliveryInstructions = JSON.parse(profile.delivery_instructions);

    if (loading) {
        return (
            <div style={styles.loading}>
                <div>Loading profile data...</div>
            </div>
        );
    }

    return (
        <div style={styles.container}>
            <h1 style={styles.title}>
                Personalisasi Profil
                <span style={styles.titleAfter}></span>
            </h1>
            
            <form onSubmit={handleSubmit} style={styles.form}>
                <div style={styles.formGroup}>
                    <label style={styles.label}>Nama Lengkap</label>
                    <input
                        type="text"
                        name="full_name"
                        value={profile.full_name}
                        onChange={handleChange}
                        style={styles.input}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
                        onBlur={(e) => e.target.style = styles.input}
                        required
                    />
                </div>

                <div style={styles.formGroup}>
                    <label style={styles.label}>Nomor Telepon</label>
                    <input
                        type="tel"
                        name="phone"
                        value={profile.phone}
                        onChange={handleChange}
                        style={styles.input}
                        onFocus={(e) => e.target.style = { ...styles.input, ...styles.inputFocus }}
                        onBlur={(e) => e.target.style = styles.input}
                        required
                    />
                </div>

                <div style={styles.formGroup}>
                    <label style={styles.label}>Alamat</label>
                    <textarea
                        name="address"
                        value={profile.address}
                        onChange={handleChange}
                        style={styles.textarea}
                        onFocus={(e) => e.target.style = { ...styles.textarea, ...styles.inputFocus }}
                        onBlur={(e) => e.target.style = styles.textarea}
                        required
                    />
                </div>

                <div style={styles.formGroup}>
                    <label style={styles.label}>Waktu Pengiriman yang Diinginkan</label>
                    <select
                        name="preferred_time"
                        value={deliveryInstructions.preferred_time}
                        onChange={handleChange}
                        style={styles.timeSelect}
                        onFocus={(e) => e.target.style = { ...styles.timeSelect, ...styles.inputFocus }}
                        onBlur={(e) => e.target.style = styles.timeSelect}
                    >
                        {deliveryTimes.map(time => (
                            <option key={time} value={time}>
                                {time} WIB
                            </option>
                        ))}
                    </select>

                    <div style={styles.radioGroup}>
                        <label style={styles.radioLabel}>
                            <input
                                type="radio"
                                name="day_preference"
                                value="weekday"
                                checked={deliveryInstructions.day_preference === 'weekday'}
                                onChange={handleChange}
                                style={styles.radioInput}
                            />
                            <span 
                                style={{
                                    ...styles.radioCustom,
                                    ...(deliveryInstructions.day_preference === 'weekday' ? styles.radioInputChecked : {})
                                }}
                            ></span>
                            Hari Kerja (Senin-Jumat)
                        </label>
                        <label style={styles.radioLabel}>
                            <input
                                type="radio"
                                name="day_preference"
                                value="weekend"
                                checked={deliveryInstructions.day_preference === 'weekend'}
                                onChange={handleChange}
                                style={styles.radioInput}
                            />
                            <span 
                                style={{
                                    ...styles.radioCustom,
                                    ...(deliveryInstructions.day_preference === 'weekend' ? styles.radioInputChecked : {})
                                }}
                            ></span>
                            Akhir Pekan (Sabtu-Minggu)
                        </label>
                    </div>
                </div>

                <button 
                    type="submit" 
                    style={styles.button}
                    onMouseEnter={(e) => e.target.style = { ...styles.button, ...styles.buttonHover }}
                    onMouseLeave={(e) => e.target.style = styles.button}
                    onMouseDown={(e) => e.target.style = { ...styles.button, ...styles.buttonHover, ...styles.buttonActive }}
                    onMouseUp={(e) => e.target.style = { ...styles.button, ...styles.buttonHover }}
                >
                    Simpan Profil
                </button>

                {error && <div style={styles.error}>{error}</div>}
                {successMessage && <div style={styles.success}>{successMessage}</div>}
            </form>
        </div>
    );
};

export default CustomerProfile;
