import React, { useState, useEffect } from 'react';
import { organizationProfileService, userService } from '../services/apiService';
import { useNavigate } from 'react-router-dom';
import '../style/orgPersonalisasiProfile.css';

const OrgPersonalisasiProfile = () => {
    const navigate = useNavigate();
    const [profile, setProfile] = useState({
        org_name: '',
        contact_person: '',
        contact_phone_org: '',
        org_type: 'retail' // default value
    });
    
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [successMessage, setSuccessMessage] = useState('');

    useEffect(() => {
        const checkAuthAndLoadProfile = async () => {
            try {
                const currentUser = userService.getCurrentUser();
                if (!currentUser || !userService.isOrganization()) {
                    navigate('/login');
                    return;
                }

                await loadProfile();
            } catch (error) {
                if (error.response?.status === 401) {
                    navigate('/login');
                } else {
                    setError(error.message || 'Error loading profile');
                }
            } finally {
                setLoading(false);
            }
        };

        checkAuthAndLoadProfile();
    }, [navigate]);

    const loadProfile = async () => {
        try {
            const response = await organizationProfileService.getOrgProfileById();
            if (response.data) {
                setProfile(response.data);
            }
        } catch (error) {
            setError(error.message || 'Error loading organization profile');
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfile(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setSuccessMessage('');
        try {
            const isLoggedIn = await userService.isLoggedIn();
            if (!isLoggedIn) {
                throw new Error('Silakan login terlebih dahulu');
            }

            const response = await organizationProfileService.createOrUpdateProfile(profile);
            
            if (response.status === 'success') {
                setSuccessMessage('Profil organisasi berhasil diperbarui!');
                await loadProfile();
            } else {
                setError(response.message || 'Terjadi kesalahan saat memperbarui profil');
            }
        } catch (error) {
            if (error.response?.status === 401) {
                navigate('/login');
            } else {
                setError(error.message || 'Error updating profile');
            }
        }
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loader"></div>
                <div>Loading profile data...</div>
            </div>
        );
    }

    return (
        <div className="org-profile-container">
            <h1 className="profile-title">
                Profil Organisasi
                <span className="title-decoration"></span>
            </h1>

            {error && <div className="error-message">{error}</div>}
            {successMessage && <div className="success-message">{successMessage}</div>}

            <form onSubmit={handleSubmit} className="profile-form">
                <div className="form-group">
                    <label className="form-label">Nama Organisasi</label>
                    <input
                        type="text"
                        name="org_name"
                        value={profile.org_name}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Contact Person</label>
                    <input
                        type="text"
                        name="contact_person"
                        value={profile.contact_person}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Nomor Telepon Organisasi</label>
                    <input
                        type="tel"
                        name="contact_phone_org"
                        value={profile.contact_phone_org}
                        onChange={handleChange}
                        className="form-input"
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Tipe Organisasi</label>
                    <select
                        name="org_type"
                        value={profile.org_type}
                        onChange={handleChange}
                        className="form-select"
                        required
                    >
                        <option value="retail">Retail</option>
                        <option value="corporate">Corporate</option>
                        <option value="education">Education</option>
                        <option value="government">Government</option>
                        <option value="non_profit">Non Profit</option>
                    </select>
                </div>

                <div className="button-container">
                    <button
                        type="button"
                        onClick={() => navigate('/dashboard')}
                        className="back-button"
                    >
                        Kembali ke Dashboard
                    </button>
                    <button 
                        type="submit" 
                        className="save-button"
                    >
                        Simpan Profil
                    </button>
                </div>
            </form>
        </div>
    );
};

export default OrgPersonalisasiProfile;
